import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Remboursement } from '../models/remboursement.model';

@Injectable({
  providedIn: 'root'
})
export class RemboursementService {
  private baseUrl = 'http://localhost:8080/api';

  constructor(private http: HttpClient) { }

  getRemboursementsByCredit(creditId: number): Observable<Remboursement[]> {
    return this.http.get<Remboursement[]>(`${this.baseUrl}/credits/${creditId}/remboursements`);
  }

  getRemboursement(id: number): Observable<Remboursement> {
    return this.http.get<Remboursement>(`${this.baseUrl}/remboursements/${id}`);
  }

  markAsPaid(id: number, dateRemboursement: Date): Observable<Remboursement> {
    return this.http.patch<Remboursement>(`${this.baseUrl}/remboursements/${id}/paid`, { dateRemboursement });
  }
}
